<script>
    document.addEventListener('DOMContentLoaded', function() {
        // Lấy đường dẫn hiện tại
        const currentPath = window.location.pathname;


        // Kiểm tra nếu đường dẫn chứa '/detail' và '/product'
        if (currentPath.includes('/detail') || currentPath.includes('/product')|| currentPath.includes('/introduce')|| currentPath.includes('/contact')) {

            // Ẩn icon search
            const searchIcon = document.getElementById('search-icon');
            const mobileSearch= document.getElementById('rmp-search-box-19793')
            if (searchIcon||mobileSearch) {
                searchIcon.style.display = 'none';
                mobileSearch.style.display = 'none';
            }
        }
    });
</script>


<script>
    // Lắng nghe sự kiện submit của form tìm kiếm


    document.addEventListener('DOMContentLoaded', function() {
        const openSearchButton = document.getElementById('openSearchPopup'); // Nút mở popup
        const searchPopup = document.getElementById('searchPopup'); // Popup tìm kiếm
        const closeSearchButton = document.getElementById('closeSearchPopup'); // Nút đóng popup

        // Hiển thị popup tìm kiếm khi nhấn vào biểu tượng tìm kiếm
        openSearchButton.addEventListener('click', function() {
            searchPopup.style.display = 'flex'; // Hiển thị popup
        });

        // Đóng popup khi nhấn vào nút đóng
        closeSearchButton.addEventListener('click', function() {
            searchPopup.style.display = 'none'; // Ẩn popup
        });

        // Đóng popup khi nhấn ra ngoài
        window.addEventListener('click', function(event) {
            if (event.target === searchPopup) {
                searchPopup.style.display = 'none';
            }
        });
    });

</script>
<style>
    /* Kiểu dáng popup tìm kiếm */
    .search-popup {
        display: none; /* Ẩn popup mặc định */
        position: fixed;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background-color: rgba(0, 0, 0, 0.7); /* Nền mờ */
        z-index: 9999;
        justify-content: center;
        align-items: center;
        animation: fadeIn 0.5s ease-out;
    }

    /* Hiệu ứng chuyển động khi popup xuất hiện */
    @keyframes fadeIn {
        0% {
            opacity: 0;
        }
        100% {
            opacity: 1;
        }
    }

    .search-popup-content {
        background-color: #fff;
        padding: 40px;
        border-radius: 10px;
        width: 80%;
        max-width: 450px;
        box-shadow: 0 4px 15px rgba(0, 0, 0, 0.3);
        display: flex;
        flex-direction: column;
        align-items: center;
        gap: 10px;
        position: relative; /* Để biểu tượng đóng nằm ở vị trí chính xác */
    }

    /* Form tìm kiếm */
    .form-search {
        display: flex;
        flex-direction: column;
        gap: 10px;
        width: 100%;
    }

    /* Input tìm kiếm */
    .input-search {
        padding: 12px;
        border: 1px solid #ddd;
        border-radius: 8px;
        width: 100%;
        font-size: 16px;
        transition: all 0.3s ease;
    }

    .input-search:focus {
        border-color: #e24725; /* Màu viền khi focus */
        outline: none;
    }

    /* Nút tìm kiếm */
    .btn-search {
        padding: 12px;
        background-color: #e24725;
        color: #fff;
        border: none;
        border-radius: 8px;
        font-size: 16px;
        cursor: pointer;
        transition: background-color 0.3s ease;
    }

    .btn-search:hover {
        background-color: #d63921;
    }

    /* Nút đóng popup */
    .container-icon-close {
        cursor: pointer;
        color: #888;
        font-size: 24px;
        position: absolute; /* Đặt vị trí của icon ở góc phải */
        top: 10px;
        right: 10px; /* Đặt icon đóng ở bên phải */
        transition: color 0.3s;
    }

    .container-icon-close:hover {
        color: #e24725;
    }

    /* Biểu tượng đóng */
    .icon-close {
        font-size: 26px;
        color: #e24725;
        cursor: pointer;
        transition: transform 0.3s;
    }

    .icon-close:hover {
        transform: scale(1.1);
    }


</style>
